import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      console.log('Uspješna prijava:', response.data.token);
      // Ovdje možete pohraniti token u localStorage ili druge metode za daljnje korištenje
    } catch (error) {
      console.error('Greška pri prijavi:', error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Prijava</h2>
      <input
        type="text"
        placeholder="Korisničko ime"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Lozinka"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Prijavi se</button>
    </div>
  );
};

export default Login;
