import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import ONama from './components/ONama';
import Ishrana from './components/Ishrana';
import Trening from './components/Trening';
import Glavna from './components/Glavna';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const PrivateRoute = ({ element, ...rest }) => {
    return isLoggedIn ? <Route {...rest} element={element} /> : <Navigate to="/login" />;
  };

  return (


    <Route {...rest}>
    {isLoggedIn ? <Glavna /> : <Navigate to="/login" />}
    <Route exact path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
    <Route exact path="/register" element={<Register />} />
    {isLoggedIn ? <ONama /> : <Navigate to="/login" />}
    {isLoggedIn ? <Ishrana /> : <Navigate to="/login" />}
    {isLoggedIn ? <Trening /> : <Navigate to="/login" />}
    </Route>



    // <Router>
      // <div>
        /* <Navigation isLoggedIn={isLoggedIn} />
        <Routes>
          <Route exact path="/" element={<Glavna />} />
          <Route exact path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
          <Route exact path="/register" element={<Register />} />
          <PrivateRoute exact path="/o-nama" element={<ONama />} />
          <PrivateRoute exact path="/ishrana" element={<Ishrana />} />
          <PrivateRoute exact path="/trening" element={<Trening />} />
        </Routes> */
      // </div>
    // </Router>
  );
};

export default App;
