import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import ONama from './components/ONama';
import Ishrana from './components/Ishrana';
import Trening from './components/Trening';
import Glavna from './components/Glavna';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <Navigation isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
        <Routes>
          <Route exact path="/" element={<Glavna />} />
          {!isLoggedIn && <Route path="/login" element={<Login handleLogin={handleLogin} />} />}
          {!isLoggedIn && <Route path="/register" element={<Register />} />}
          {isLoggedIn && <Route path="/o-nama" element={<ONama />} />}
          {isLoggedIn && <Route path="/ishrana" element={<Ishrana />} />}
          {isLoggedIn && <Route path="/trening" element={<Trening />} />}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
