import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import ONama from './components/ONama';
import Ishrana from './components/Ishrana';
import Trening from './components/Trening';
import Glavna from './components/Glavna';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if the user is logged in based on token availability in localStorage
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <Navigation isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
        <Routes>
          <Route exact path="/" element={<Glavna />} />
          <Route
            exact
            path="/login"
            element={!isLoggedIn ? <Login handleLogin={handleLogin} /> : <Navigate to="/" />}
          />
          <Route
            exact
            path="/register"
            element={!isLoggedIn ? <Register /> : <Navigate to="/" />}
          />
          <Route
            exact
            path="/o-nama"
            element={isLoggedIn ? <ONama /> : <Navigate to="/login" />}
          />
          <Route
            exact
            path="/ishrana"
            element={isLoggedIn ? <Ishrana /> : <Navigate to="/login" />}
          />
          <Route
            exact
            path="/trening"
            element={isLoggedIn ? <Trening /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
