// App.js

import React, { useEffect } from 'react';

function AboutUs() {
  useEffect(() => {
    // Funkcija za učitavanje Google mape
    const loadMap = () => {
      // Učitavanje Google Maps API
      const googleScript = document.createElement('script');
      googleScript.src = `https://maps.googleapis.com/maps/api/js?key=TVOJ_API_KLJUC&libraries=places`;
      window.document.body.appendChild(googleScript);

      googleScript.addEventListener('load', () => {
        // Inicijalizacija mape
        const mapOptions = {
          center: { lat: 44.7866, lng: 20.4489 }, // Centar mape (Beograd)
          zoom: 8, // Povećanje nivoa zumiranja
        };
        new window.google.maps.Map(document.getElementById('map'), mapOptions);
      });
    };

    loadMap();
  }, []);

  return (
    <div className="about-us">
      <div className="about-us-content">
        <h1>O nama</h1>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed vestibulum turpis ac ex
          fringilla posuere. Quisque quis semper mauris. Nullam nec consequat libero. Donec
          elementum velit vel justo eleifend, vel accumsan justo congue.
        </p>
        {/* Dodajte više teksta o vašem fitnesu ovde */}

        <div id="map" className="map"></div>
      </div>
    </div>
  );
}

export default AboutUs;
