// Trening.js

import React, { useState } from 'react';

const Trening = () => {
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [bmi, setBMI] = useState(null);

  const calculateBMI = () => {
    // Logika za izračun BMI
    const heightMeters = height / 100; // Pretvori visinu u metre
    const bmiValue = (weight / (heightMeters * heightMeters)).toFixed(2);
    setBMI(bmiValue);
  };

  return (
    <div>
      <h1>Stranica za Trening</h1>
      <label>
        Visina (cm):
        <input type="number" value={height} onChange={(e) => setHeight(e.target.value)} />
      </label>
      <br />
      <label>
        Težina (kg):
        <input type="number" value={weight} onChange={(e) => setWeight(e.target.value)} />
      </label>
      <br />
      <button onClick={calculateBMI}>Izračunaj BMI</button>
      {bmi && <p>Vaš BMI je: {bmi}</p>}
      {/* Dodajte ostale komponente ili logiku za trening */}
    </div>
  );
};

export default Trening;
