import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import ONama from './components/ONama';
import Ishrana from './components/Ishrana';
import Trening from './components/Trening';
import Glavna from './components/Glavna';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const AuthRoute = ({ element: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        element={isLoggedIn ? <Component /> : <Navigate to="/login" />}
      />
    );
  };

  const UnauthRoute = ({ element: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        element={!isLoggedIn ? <Component /> : <Navigate to="/" />}
      />
    );
  };

  return (
    <Router>
      <div>
        <Navigation />
        <Routes>
          <Route
            exact
            path="/"
            element={<AuthRoute element={Glavna} />}
          />
          <Route
            exact
            path="/login"
            element={<UnauthRoute element={() => <Login setIsLoggedIn={setIsLoggedIn} />} />}
          />
          <Route
            exact
            path="/register"
            element={<UnauthRoute element={Register} />}
          />
          <Route
            exact
            path="/o-nama"
            element={<AuthRoute element={ONama} />}
          />
          <Route
            exact
            path="/ishrana"
            element={<AuthRoute element={Ishrana} />}
          />
          <Route
            exact
            path="/trening"
            element={<AuthRoute element={Trening} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
