import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import ONama from './components/ONama';
import Ishrana from './components/Ishrana';
import Trening from './components/Trening';
import Glavna from './components/Glavna';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const AuthRoute = ({ element, ...rest }) => {
    if (isLoggedIn) {
      return <Route {...rest} element={element} />;
    } else {
      return <Navigate to="/login" />;
    }
  };

  const UnauthRoute = ({ element, ...rest }) => {
    if (!isLoggedIn) {
      return <Route {...rest} element={element} />;
    } else {
      return <Navigate to="/" />;
    }
  };

  return (
    <Router>
      <div>
        <Navigation />
        <Routes>
          <AuthRoute exact path="/" element={<Glavna />} />
          <UnauthRoute exact path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
          <UnauthRoute exact path="/register" element={<Register />} />
          <AuthRoute exact path="/o-nama" element={<ONama />} />
          <AuthRoute exact path="/ishrana" element={<Ishrana />} />
          <AuthRoute exact path="/trening" element={<Trening />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
