import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import ONama from './components/ONama';
import Ishrana from './components/Ishrana';
import Trening from './components/Trening';
import Glavna from './components/Glavna';
import Login from './components/Login';
import Register from './components/Register';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if the user is logged in based on token availability in localStorage
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (token) => {
    localStorage.setItem('token', token);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };

  const PrivateRoute = ({ element, ...rest }) => {
    return isLoggedIn ? element : <Navigate to="/login" />;
  };

  const PublicRoute = ({ element, ...rest }) => {
    return !isLoggedIn ? element : <Navigate to="/" />;
  };

  return (
    <Router>
      <div>
        <Navigation isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
        <Routes>
          <Route exact path="/" element={<Glavna />} />
          <PublicRoute exact path="/login" element={<Login handleLogin={handleLogin} />} />
          <PublicRoute exact path="/register" element={<Register />} />
          <PrivateRoute exact path="/o-nama" element={<ONama />} />
          <PrivateRoute exact path="/ishrana" element={<Ishrana />} />
          <PrivateRoute exact path="/trening" element={<Trening />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
